pragma solidity ^0.4.18;

contract Merchant {

    /* 1. the merchant scans for the refugee's key (implemented in Javascript)
     * 2. once the key is found, the merchant then retrieves refugee's picture 
     *    or fingerprint for verification (also implemented in Javascript)
     * 3. if verification is successful, the merchant approves the token purchse
     *    provided the bill is less than the token balance of the refugee (implemented in javascript)
     * 4. if the transaction is approved, it is added to the tokenTransactions database on the blockchain
     * 5. once the transaction is recorded, an event is triggered so that the tokens are burnt and the 
     *    refugee balance is reduced from web3js
     * 6. merchant applies for registration from the front end */

    event TokenTransactionAdded (uint _totalBill, uint _refugeeID, uint _merchantID, string _purchaseDescription);
    TokenTransaction[] tokenTransactions;
    MerchantProfile[] merchants;

    struct TokenTransaction {
        uint totalBill;
        uint refugeeID;
        uint merchantID;
        string purchaseDescription;
    }

    struct MerchantProfile {
        uint bankAccountNumber;
        string name;
        string bankNameAndBranch;
        string phone;
        string merchantAddress;
        string password;  //merchant password to be encrypted from the front-end
    }

    /*recordTokenTransaction takes the total bill and purchase description 
    from the front end and adds it to the blockchain*/
    function recordTokenTransaction (uint _totalBill, uint _refugeeID, uint _merchantID, string _purchaseDescription) public {
        tokenTransactions.push(TokenTransaction(_totalBill, _refugeeID, _merchantID, _purchaseDescription));
        TokenTransactionAdded(_totalBill, _refugeeID, _merchantID, _purchaseDescription);
    }

    function registerAsMerchant (uint _bankAccountNumber, string _name, string _bankNameAndBranch, string _phone, string _merchantAddress, string _password) external {
        //adds to merchants database
        merchants.push(MerchantProfile(_bankAccountNumber, _name, _bankNameAndBranch, _phone, _merchantAddress, _password));
    }
}
